import {
    Button,
    Layout,
    Page,
    Form,
    TextField,
    FormLayout
} from '@shopify/polaris';


class Login extends React.Component {
    constructor(props){
        super(props)

        this.state = {
            username: "",
            password: ""
        }

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    render() {
        return (
            <Page>
                <Layout>
                    <Form>
                        <FormLayout>
                            <TextField
                                value={this.username}
                                onChange={this.handleChange}
                                label="User Name"
                                type="text"
                                name="username"
                            />

                            <TextField
                                value={this.password}
                                onChange={this.handleChange}
                                label="Password"
                                type="password"
                            />

                            <Button>Sign In</Button>
                            <Button>Register</Button>
                        </FormLayout>
                    </Form>
                </Layout>
            </Page>
        );
    }
}

export default Login;
