import {
    Button,
    Layout,
    Page,
    Form,
    TextField,
    FormLayout
} from '@shopify/polaris';


class Login extends React.Component {
    state = {
        username: "",
        password: ""
    }

    handleChange = (e) => {
        console.log(e.target)
    }

    render() {
        return (
            <Page>
                <Layout>
                    <Form>
                        <FormLayout>
                            <TextField
                                value={this.username}
                                onChange={this.handleChange}
                                label="User Name"
                                type="text"
                            />

                            <TextField
                                value={this.password}
                                onChange={this.handleChange}
                                label="Password"
                                type="password"
                            />

                            <Button>Sign In</Button>
                        </FormLayout>
                    </Form>

                    <Form>
                        <FormLayout>
                            
                        </FormLayout>
                    </Form>
                </Layout>
            </Page>
        );
    }
}

export default Login;
