import {
    Button,
    Layout,
    Page,
    Form,
    TextField
} from '@shopify/polaris';


class Login extends React.Component {
    
    state = {
        username: ""
    }

    handleChange = (e) => {
        console.log(e.target)
     }

    render() {
        return (
            <Page>
                <Layout>
                    <Form>
                        <TextField
                            value={username}
                            onChange={handleChange}
                            label="User Name"
                            type="text"
                        />
                    </Form>
                </Layout>
            </Page>
        );
    }
}

export default Login;
